class Solution{
    public int[] sortArray(int[] nums){
        int len = nums.length;
        int min = nums[0];
        int max = nums[0];
        for (int i = 0; i < len; i++) {
            min = Math.min(min, nums[i]);
            max = Math.max(max, nums[i]);
        }
        int range = max - min + 1;
        int[] bucket = new int[range];
        for (int i = 0; i < len; i++) {
            bucket[nums[i] - min]++;
