class Solution {
    public int candy(int[] ratings) {
        int len=ratings.length;
        int[]arr=new int[len];
        Arrays.fill(arr,1);
        for (int i = 1; i < ratings.length; i++) {
            if(ratings[i]>ratings[i-1]){
                arr[i]=arr[i-1]+1;
            }
