```java
class Solution {
    public ListNode reverseList(ListNode head) {
        ListNode last=null,cur=head,temp=cur;
        while(cur!=null){
            temp=cur.next;
            cur.next=last;
            last=cur;
            cur=temp;
        }
        return last;
    }
}
