        for (int i = 0; i < len; i++) {
            if(lists[i]==null){
                continue;
            }
            ListNode ptr=lists[i];
            while(ptr!=null){
                heap.offer(ptr.val);
                ptr=ptr.next;
            }
        }
        if(!heap.isEmpty()){
            ListNode res=new ListNode(heap.poll());
            ListNode ptr=res;
            while (!heap.isEmpty()){
                ListNode node=new ListNode(heap.poll());
                ptr.next=node;
                ptr=ptr.next;
            }
            return res;
        }else{
            return null;
        }
    }
}
