class Solution{
    public int[] sortArray(int[] nums){
        int len= nums.length;
        for (int gap = len / 2; gap > 0; gap /= 2) {
            // å…±gapä¸ªç»„ï¼Œå¯¹æ¯ä¸€ç»„éƒ½æ‰§è¡Œç›´æ¥æ’å…¥æ’åº
            for (int i = 0 ;i < gap; i++) {
                for (int j = i + gap; j < len; j += gap) {
                    // å¦‚æœnums[j] < nums[j-gap]ï¼Œåˆ™å¯»æ‰¾nums[j]ä½ç½®ï¼Œå¹¶å°†åé¢æ•°æ®çš„ä½ç½®éƒ½åç§»ã€‚
                    if (nums[j] < nums[j - gap]) {
                        int tmp = nums[j];
                        int k = j - gap;
                        while (k >= 0 && nums[k] > tmp) {
                            nums[k + gap] = nums[k];
                            k -= gap;
                        }
                        nums[k + gap] = tmp;
                    }
                }
            }
        }
        return nums;
    }
}
