        for (int i = 0; i < nums.length; i++) {
            numbers[i] = nums[i];
        }
        Arrays.sort(numbers, comparator);
        StringBuilder sb=new StringBuilder();
        int flag=0;
        for (int i = 0; i < numbers.length; i++) {
            if(numbers[i]!=0||flag==1){
                flag=1;
                sb.append(numbers[i]);
            }
        }
        if(sb.isEmpty()){
            return "0";
        }else{
            return sb.toString();
        }
    }
}
