class Solution {
    public int[] distinctDifferenceArray(int[] nums) {
        Set<Integer> set = new HashSet<>();
        int n = nums.length;
        int[] sum = new int[n];
        for (int i = 0; i < n; i++) {
            set.add(nums[i]); 
            sum[i] = set.size();
        } 
        set.clear(); 
        for (int i = n - 1; i >= 0; i--) { 
            sum[i] = sum[i] - set.size(); 
            set.add(nums[i]);
        }
        return sum;
    } 
}
