class Solution {
    public String removeKdigits(String num, int k) {
        int len=num.length();
        Stack<Character>stack=new Stack<>();
        for (int i = 0; i < len; i++) {
            char ch=num.charAt(i);
            while (!stack.isEmpty()&&k>0&&stack.peek()>ch){
                stack.pop();
                k--;
            }
            stack.push(num.charAt(i));
