            stack.pop();
        }
        StringBuilder res=new StringBuilder();
        int size=stack.size();
        boolean flag=true;
        for (Character character : stack) {
            if(character=='0'&&flag){
                continue;
            }
            if(character!='0'){
                flag=false;
            }
            res.append(character);
        }
        return res.isEmpty()?"0":res.toString();
    }
}
