class Solution {
    public ListNode deleteDuplicates(ListNode head) {
        if(head==null||head.next==null){
            return head;
        }
        ListNode last=head,p=last.next;
        while(p.next!=null) {//pä¸æ˜¯æœ€åä¸€ä¸ª
            if (p.val == last.val) {
                last.next = p.next;
            }else{
                last = p;
            }
            p = p.next;
        }
        if(p.val==last.val){
            last.next=null;
        }
        return head;
    }
}
