class Solution {
    public long findMaximumNumber(long k, int x) {
        long res=1;
        long sum=0;
        while (sum<=k){
            long count=func(res,x);
            sum+=count;
            if(sum>k){
                return res-1;
            }
            res++;
        }
        return res;
    }
    long func(long num,int k){
        long sum=0;
        String binary = Long.toBinaryString(num);
        for (int i = binary.length()-k; i>= 0; i-=k) {
            sum+=  Long.parseLong(binary.charAt(i)+"");
        }
        return sum;
    }
}
