class Solution {
    public void solveSudoku(char[][] board) {
        backtraking(board);
        return;
    }
    boolean backtraking(char[][] board){
        for (int i = 0; i < 9; i++) {
            for (int j = 0; j < 9; j++) {
                if(board[i][j]!='.'){
                    continue;
                }else{
                    for (char k = '1'; k <= '9'; k++) {
                        if(check(i,j,k,board)){
                            board[i][j]=k;
                            if(backtraking(board)){
                                return true;
                            }
                            board[i][j]='.';
                        }
                    }
                    return false;
                }
            }
        }
        return true;
    }
    boolean check(int i,int j,char k,char[][] board){
        for (int l = 0; l < 9; l++) {
            if(board[i][l]==k){
                return false;
            }
            if(board[l][j]==k){
                return false;
            }
        }
        int row=(i/3)*3,col=(j/3)*3;
        for (int l = 0; l < 3; l++) {
            for (int m = 0; m < 3; m++) {
                if(board[row+l][col+m]==k){
                    return false;
                }
            }
        }
        return true;
    }
}
