        this.in.offer(x);
    }

    public int pop() {
        int temp=this.in.peek();
        while (!this.in.isEmpty()){
            temp=this.in.peek();
            if(this.in.size()==1){
                this.in.poll();
            }else {
                this.out.offer(this.in.poll());
            }
        }
        while (!this.out.isEmpty()){
            this.in.offer(this.out.poll());
        }
        return temp;
    }

    public int top() {
        int temp=this.in.peek();
        while (!this.in.isEmpty()){
            temp=this.in.peek();
            this.out.offer(this.in.poll());
        }
        while (!this.out.isEmpty()){
            this.in.offer(this.out.poll());
        }
        return temp;
    }

    public boolean empty() {
        return this.in.isEmpty();
    }

}
