class Solution {
    int[]inOrder;
    int[]postOrder;
    int len;
    public TreeNode buildTree(int[] inorder, int[] postorder) {
        inOrder=inorder;
        postOrder=postorder;
        len= inorder.length;
        TreeNode res=new TreeNode(postOrder[len-1]);
        recursion(res,len-1,0,len-1);
        return res;
    }
    void recursion(TreeNode root,int base,int left,int right){
        if(root==null||left>=right){
            return;
        }
        int i=left;
        while (i<right&&inOrder[i]!=postOrder[base]){
            i++;
        }
        int left_cnt=i-left;
        int right_cnt=right-i;
        if(left_cnt>0){
            root.left=new TreeNode(postOrder[base-right_cnt-1]);
            recursion(root.left,base-right_cnt-1,left,i-1);
        }
        if(right_cnt>0){
            root.right=new TreeNode(postOrder[base-1]);
            recursion(root.right,base-1,i+1,right);
        }
    }
}
```

