class Solution{
    public int[]sortArray(int[]nums){
        int len=nums.length;
        quickSort(nums,0,len-1);
        return nums;
    }
    public void quickSort(int[]nums,int left,int right){
        if(left<right){
            int i=left;
            int j=right;
            int base=nums[i];
            while(i<j){
                while(i<j&&nums[j]>base){
                    j--;
                }
                if(i<j){
                    nums[i++]=nums[j];
                }
                while(i<j&&nums[i]<base){
                    i++;
                }
                if(i<j){
                    nums[j--]=nums[i];
                }
            }
            nums[i]=base;
            quickSort(nums,left,i-1);
            quickSort(nums,i+1,right);
        }
    }
}
```

