                        int tmp = nums[j];
                        int k = j - gap;
                        while (k >= 0 && nums[k] > tmp) {
                            nums[k + gap] = nums[k];
                            k -= gap;
                        }
                        nums[k + gap] = tmp;
                    }
                }
            }
        }
        return nums;
    }
}
