class Solution{
    public int[] sortArray(int[] nums){
        int len = nums.length;
        mergeSort(nums,0,len-1);
        return nums;
    }
    void mergeSort(int[] arr, int start, int end) {
        //ÂàÜÊ≤ªÁöÑÁªìÊùüÊù°‰ª∂
        if (start >= end) {
            return;
        }
        //‰øùËØÅ‰∏çÊ∫¢Âá∫ÂèñstartÂíåendÁöÑ‰∏≠‰ΩçÊï∞
        int mid = start + ((end - start) >> 1);
        //ÈÄíÂΩíÊéíÂ∫èÂπ∂‰∏îÂêàÂπ∂
        mergeSort(arr, start, mid);
        mergeSort(arr, mid + 1, end);
        merge(arr, start, mid, end);
    }
    void merge(int[] arr, int start, int mid, int end) {
        int[] temp = new int[end - start + 1];
        int p1 = start;
        int p2 = mid + 1;
        int p = 0;
        while (p1 <= mid && p2 <= end) {
            if (arr[p1] > arr[p2]) {
                temp[p++] = arr[p2++];
            } else {
                temp[p++] = arr[p1++];
            }
        }
        while (p1 <= mid) {
            temp[p++] = arr[p1++];
        }
        while (p2 <= end) {
            temp[p++] = arr[p2++];
        }
        for (int i = 0; i < temp.length; i++) {
            arr[i + start] = temp[i];
        }
    }
}
