class Solution {
    public int[] sortArray(int[] nums) {
        // ÊâæÂà∞Êï∞ÁªÑ‰∏≠ÁöÑÊúÄÂ§ßÂÄºÂíåÊúÄÂ∞èÂÄº
        int min = Arrays.stream(nums).min().getAsInt();
        int max = Arrays.stream(nums).max().getAsInt();
        // ËÆ°ÁÆóËÆ°Êï∞Êï∞ÁªÑÁöÑÈïøÂ∫¶
        int length = max - min + 1;
        int[] count = new int[length];
        // ÁªüËÆ°ÊØè‰∏™ÂÖÉÁ¥†ÁöÑÂá∫Áé∞Ê¨°Êï∞
        for (int num : nums) {
            count[num - min]++;
        }
        // Â∞ÜËÆ°Êï∞Êï∞ÁªÑËΩ¨Êç¢‰∏∫ÂâçÁºÄÂíåÊï∞ÁªÑ
        for (int i = 1; i < length; i++) {
            count[i] += count[i - 1];
        }
        // ÂàõÂª∫‰∏¥Êó∂Êï∞ÁªÑÂ≠òÂÇ®ÊéíÂ∫èÂêéÁöÑÁªìÊûú
        int[] res = new int[nums.length];
        // Â∞ÜÂÖÉÁ¥†ÊåâÁÖßËÆ°Êï∞Êï∞ÁªÑÁöÑÂâçÁºÄÂíåÂàÜÈÖçÂà∞‰∏¥Êó∂Êï∞ÁªÑ
        for (int i = nums.length - 1; i >= 0; i--) {
            int index = count[nums[i] - min] - 1;
            res[index] = nums[i];
            count[nums[i] - min]--;
        }
        return res;
    }
}
