private Object[] grow(int minCapacity) {
    //Ëé∑ÂèñÂΩìÂâçÊï∞ÁªÑÁöÑÂÆπÈáè oldCapacity
    int oldCapacity = elementData.length;
    //Â¶ÇÊûúÂΩìÂâçÊï∞ÁªÑÂÆπÈáèÂ§ß‰∫é 0 ÊàñËÄÖÂΩìÂâçÊï∞ÁªÑ‰∏çÊòØ‰ΩøÁî®ÈªòËÆ§ÂÆπÈáèÁöÑÁ©∫Êï∞ÁªÑÔºåÂàôËÆ°ÁÆóÊñ∞ÁöÑÂÆπÈáèÂ§ßÂ∞è newCapacity
    if (oldCapacity > 0 || elementData != DEFAULTCAPACITY_EMPTY_ELEMENTDATA) {
        //Êñ∞ÁöÑÂÆπÈáèÂ§ßÂ∞èÁî± ArraysSupport.newLength() ÊñπÊ≥ïÂÜ≥ÂÆöÔºåÂÆÉÊ†πÊçÆÊúÄÂ∞èÂ¢ûÈïøÈúÄÊ±Ç minCapacity - oldCapacity ÂíåÂêàÈÄÇÁöÑÂ¢ûÈïøÁéá oldCapacity >> 1ÔºàÂΩìÂâçÂÆπÈáèÁöÑ‰∏ÄÂçäÔºâÊù•Á°ÆÂÆöÊñ∞ÁöÑÂÆπÈáèÂ§ßÂ∞è
        int newCapacity = ArraysSupport.newLength(oldCapacity,
                         minCapacity - oldCapacity, /* ÊúÄÂ∞èÂ¢ûÈïøÈúÄÊ±Ç */ 
                         oldCapacity >> 1 /* ÂêàÈÄÇÁöÑÂ¢ûÈïøÁéá*/);
        //Â∞ÜÊóßÊï∞ÁªÑÁöÑÂÖÉÁ¥†Â§çÂà∂Âà∞Êñ∞Êï∞ÁªÑÔºåÂπ∂Â∞ÜÊñ∞Êï∞ÁªÑËµãÂÄºÁªô elementData„ÄÇÊúÄÂêéÔºåËøîÂõûÊñ∞ÁöÑÊï∞ÁªÑ„ÄÇ
        return elementData = Arrays.copyOf(elementData, newCapacity);
    } else {
        //
        return elementData = new Object[Math.max(DEFAULT_CAPACITY, minCapacity)];
    }
}
private Object[] grow() {
    //ÊòØ grow(int minCapacity) ÊñπÊ≥ïÁöÑÁÆÄÂåñÁâàÊú¨ÔºåÂÆÉË∞ÉÁî® grow(size + 1) Êù•Ê†πÊçÆ ArrayList ÁöÑÂΩìÂâçÂÖÉÁ¥†Êï∞ÈáèËøõË°åÊâ©ÂÆπ„ÄÇËøôÊ†∑ÂèØ‰ª•Á°Æ‰øùÊúâË∂≥Â§üÁöÑÁ©∫Èó¥Êù•Ê∑ªÂä†‰∏Ä‰∏™Êñ∞ÂÖÉÁ¥†
    return grow(size + 1);
}
