class Solution {
    public int getMinimumDifference(TreeNode root) {
        int res=Integer.MAX_VALUE;
        Stack<TreeNode>s=new Stack<>();
        s.push(root);
        int last=root.val;
        TreeNode p=root;
        while(p.left!=null){
            p=p.left;
        }
        while(!s.isEmpty()){
            TreeNode node=s.peek();
            if(node!=null){
                s.pop();
                if(node.right!=null){
                    s.push(node.right);
                }
                s.push(node);
                s.push(null);
                if(node.left!=null){
                    s.push(node.left);
                }
            }else{
                s.pop();
                node=s.pop();
                if(Math.abs(last- node.val)<res&&Math.abs(last- node.val)!=0){
                    res=Math.abs(last- node.val);
                }
                last= node.val;
            }
        }
        return res;
    }
}
```



