class Solution {
    TreeNode res;
    public TreeNode constructMaximumBinaryTree(int[] nums) {
        res=new TreeNode(nums[getMaxIndex(nums,0, nums.length-1)]);
        dfs(res,nums,0,nums.length-1,getMaxIndex(nums,0, nums.length-1));
        return res;
    }
    void dfs(TreeNode node,int[] arr,int from,int to,int index){
        if(from<index){
            node.left=new TreeNode(arr[getMaxIndex(arr,from,index-1)]);
        }
        if(index<to){
            node.right=new TreeNode(arr[getMaxIndex(arr,index+1,to)]);
        }
        if(node.left!=null){
            dfs(node.left,arr,from,index-1,getMaxIndex(arr,from,index-1));
        }
        if(node.right!=null){
            dfs(node.right,arr,index+1,to,getMaxIndex(arr,index+1,to));
        }
    }
    int getMaxIndex(int[]arr,int i,int j){
        if(i==j){
            return i;
        }
        int maxindex=i;
        for (int k = i+1; k <= j; k++) {
            if(arr[maxindex]<arr[k]){
                maxindex=k;
            }
        }
        return maxindex;
    }
}
```

