class MyQueue {
    Stack<Integer>in;
    Stack<Integer>out;
    public MyQueue() {
        this.in=new Stack<>();
        this.out=new Stack<>();
    }

    public void push(int x) {
        this.in.push(x);
    }

    public int pop() {
        in2out();
        int temp=this.out.pop();
        out2in();
        return temp;
    }

    public int peek() {
        in2out();
        int temp=this.out.peek();
        out2in();
        return temp;
    }

    public boolean empty() {
        return this.in.isEmpty();
    }
    void in2out(){
        while (!this.in.isEmpty()){
            this.out.push(this.in.pop());
        }
    }
    void out2in(){
        while (!this.out.isEmpty()){
            this.in.push(this.out.pop());
        }
    }
}
