class TripleInOne {
    int[][]stack;
    int[]size;
    public TripleInOne(int stackSize) {
        this.stack=new int[3][stackSize];
        this.size=new int[3];
    }

    public void push(int stackNum, int value) {
        if(size[stackNum]<this.stack[stackNum].length){
            this.stack[stackNum][size[stackNum]]=value;
            this.size[stackNum]++;
        }
    }

    public int pop(int stackNum) {
        if(this.size[stackNum]>0){
            this.size[stackNum]--;
            return this.stack[stackNum][this.size[stackNum]];
        }else {
            return -1;
        }
    }

    public int peek(int stackNum) {
        if(this.size[stackNum]>0){
            return this.stack[stackNum][this.size[stackNum]-1];
        }else {
            return -1;
        }
    }

    public boolean isEmpty(int stackNum) {
        return this.size[stackNum]==0;
    }
}
```



