class Solution {
    public int[] twoSum(int[] nums, int target) {
        int[]arr=nums.clone();
        Arrays.sort(arr);
        int i=0,j= nums.length-1;
        while (i<j){
            int sum=arr[i]+arr[j];
            if(sum<target){
                i++;
            }else if(sum>target){
                j--;
            }else {
                int k=findIndex(nums,arr[i],0);
                int l=findIndex(nums,arr[j],1);
                return new int[]{k,l};
            }
        }
        return new int[]{-1,-1};
    }
    int findIndex(int[]arr,int target,int flag){
        if(flag==0){
            for (int i = 0; i < arr.length; i++) {
                if(arr[i]==target){
                    return i;
                }
            }
        }else {
            for (int i = arr.length-1; i >= 0; i--) {
                if(arr[i]==target){
                    return i;
                }
            }
        }
        return -1;
    }
}
```

