class MyLinkedList {
    ListNode head;
    int size;

    public MyLinkedList() {
        this.head=null;
        this.size=0;
    }

    public int get(int index) {
        if(index<0||index>=this.size){
            return -1;
        }else {
            ListNode ptr=this.head;
            int i=0;
            while (i<index){//iÊåáÂêëindexËäÇÁÇπ
                ptr=ptr.next;
                i++;
            }
            return ptr.val;
        }
    }

    public void addAtHead(int val) {
        ListNode node=new ListNode(val);
        if(this.head!=null) {
            node.next = this.head;
        }
        this.head=node;
        this.size++;
    }

    public void addAtTail(int val) {
        ListNode node=new ListNode(val);
        if(this.head==null){
            this.head=node;
        }else {
            ListNode ptr=this.head;
            int i=0;
            while (i<this.size-1){//iÊåáÂêëÊúÄÂêéËäÇÁÇπ
                ptr=ptr.next;
                i++;
            }
            ptr.next=node;
        }
        this.size++;
    }

    public void addAtIndex(int index, int val) {
        if(index>this.size||index<0){
            return;
        } else if(index==this.size){
            addAtTail(val);
        }else if(index==0){
            addAtHead(val);
        }else {
            int i=0;
            ListNode ptr=this.head;
            while (i<index-1){//iÊåáÂêëindex‰πãÂâç‰∏Ä‰∏™ËäÇÁÇπ
                ptr=ptr.next;
                i++;
            }
            ListNode node=new ListNode(val);
            node.next=ptr.next;
            ptr.next=node;
            this.size++;
        }
    }

    public void deleteAtIndex(int index) {
        if(index<0||index>=this.size){
            return;
        }else if(index==0){
            this.head=this.head.next;
            this.size--;
        } else {
            int i=0;
            ListNode ptr=this.head;
            while (i<index-1){//iÊåáÂêëindex‰πãÂâç‰∏Ä‰∏™ËäÇÁÇπ
                ptr=ptr.next;
                i++;
            }
            ptr.next=ptr.next.next;
            this.size--;
        }
    }
}
