class Solution {
    public ListNode removeElements(ListNode head, int val) {
        while(head!=null&&head.val==val){
            head=head.next;
        }
        ListNode p=head;
        while(p!=null){
            while(p.next!=null&&p.next.val==val){
                p.next=p.next.next;
            }
            p=p.next;
        }
        return head;
    }
}
