class Solution {
    public ListNode addTwoNumbers(ListNode l1, ListNode l2) {
        int cout = 0;
        ListNode ptr1 = l1, ptr2 = l2;
        ListNode res = new ListNode(0);
        ListNode ptr = res;
        while (ptr1 != null && ptr2 != null) {
            int sum = cout + ptr1.val + ptr2.val;
            if (sum > 9) {
                cout = 1;
                sum -= 10;
            } else {
                cout = 0;
