            ptr.next = new ListNode(sum);
            ptr = ptr.next;
            ptr1 = ptr1.next;
            ptr2 = ptr2.next;
        }
        ListNode rest = ptr1 != null ? ptr1 : ptr2;
        while (rest != null) {
            int sum = cout + rest.val;
            if (sum > 9) {
                cout = 1;
                sum -= 10;
            } else {
                cout = 0;
            }
            ptr.next = new ListNode(sum);
