class Solution {
    public ListNode removeNthFromEnd(ListNode head, int n) {
        if(head.next==null){
            return null;
        }
        ListNode fast=head,slow=fast;
        for (int i = 0; i < n; i++) {
            fast=fast.next;
        }
        if(fast==null){
            return head.next;
        }else {
            fast=fast.next;
            while (fast!=null){
                slow=slow.next;
                fast=fast.next;
            }
            slow.next=slow.next.next;
            return head;
        }
    }
}
```

