class Solution {
    public int largestSumAfterKNegations(int[] nums, int k) {
        Arrays.sort(nums);
        int res=0;
        for (int i = 0; i < nums.length; i++) {
            if(nums[i]<0&&k>0){
                nums[i]=(-1)*nums[i];
                k--;
            } else if (k>0&&k%2==1) {
                Arrays.sort(nums);
                nums[0]=(-1)*nums[0];                
                k=0;
            }
        }
        if(k>0&&k%2==1){
            Arrays.sort(nums);
            nums[0]=(-1)*nums[0];
        }
        for (int i = 0; i < nums.length; i++) {
            res+=nums[i];
        }
        return res;
    }
}
