class Solution {
    public boolean lemonadeChange(int[] bills) {
        boolean  res=true;
        int cnt_5=0,cnt_10=0;
        for (int i = 0; i < bills.length; i++) {
            if(bills[i]==5){
                cnt_5++;
            }else if(bills[i]==10){
                if(cnt_5>0){
                    cnt_5--;
                    cnt_10++;
                }else{
                    res=false;
                    break;
                }
            }else{
                if(cnt_5>0&&cnt_10>0){
                    cnt_5--;
                    cnt_10--;
                }else if(cnt_5>2){
                    cnt_5-=3;
                }else{
                    res=false;
                    break;
