class Solution {
    public int maxProfit(int[] prices) {
        if(prices.length==1){
            return 0;
        }
        if(prices.length==2){
            if(prices[0]<prices[1]){
                return prices[1]-prices[0];
            }else{
                return 0;
            }
        }
        int res=0;
        for (int i = 1; i < prices.length-1; i++) {
            if(prices[i-1]<prices[i]){//â†‘
                if(prices[i]<prices[i+1]){//â†‘â†‘
                    continue;
                }else{//â†‘â†“
                    res+=prices[i];
                }
            }else{//â†“
                if(prices[i]<prices[i+1]){//â†“â†‘
                    res-=prices[i];
                }else{//â†“â†“
                    continue;
                }
            }
        }
        if(prices[0]<prices[1]){
            res-=prices[0];
        }
        if(prices[prices.length-2]<prices[prices.length-1]){
            res+=prices[prices.length-1];
        }
        return res;
    }
}
