class Solution {
    public List<Integer> inorderTraversal(TreeNode root) {
        List<Integer> res = new LinkedList<>();
        Stack<TreeNode> s = new Stack<>();
        TreeNode cur=root;
        while(cur!=null||!s.isEmpty()){
            if(cur!=null){
                s.push(cur);
                cur=cur.left;
            }else{
                cur=s.pop();
                res.add(cur.val);
                cur=cur.right;
            }
        }
        return res;
    }
}
