class Solution {
    public List<Integer> largestValues(TreeNode root) {
        List<Integer> res=new LinkedList<>();
        if(root==null){
            return res;
        }
        Queue<TreeNode>q=new LinkedList<>();
        q.offer(root);
        while(!q.isEmpty()){
            int len=q.size();
            int max=Integer.MIN_VALUE;
            while(len-->0){
                TreeNode node=q.poll();
                if(node.val>max){
                    max=node.val;
                }
                if(node.left!=null){
                    q.offer(node.left);
                }
                if(node.right!=null){
                    q.offer(node.right);
                }
            }
            res.add(max);
        }
        return res;
    }
}
```

