class Solution {

    boolean res=false;

    public boolean hasPathSum(TreeNode root, int targetSum) {
        if(root==null){
            return res;
        }
        backtracking(root,root.val,targetSum);
        return res;
    }

    void backtracking(TreeNode root,int sum,int target){
        if(res==true){
            return;
        }
        if(sum==target&&root.left==null&&root.right==null){
            res=true;
            return;
        }
        if(root==null){
            return;
        }
        if(root.left!=null){
            sum+=root.left.val;
            backtracking(root.left,sum,target);
            sum-=root.left.val;
        }
        if(root.right!=null){
            sum+=root.right.val;
            backtracking(root.right,sum,target);
            sum-=root.right.val;
        }
    }
}
