class Solution {
    public TreeNode invertTree(TreeNode root) {
        if(root==null){
            return root;
        }
        swap(root);
        invertTree(root.left);
        invertTree(root.right);
        return root;
    }
    void swap(TreeNode r){
        TreeNode t=r.left;
        r.left=r.right;
        r.right=t;
    }
}
```

