class Solution {
    public boolean isValidBST(TreeNode root) {
        List<Integer>list=new ArrayList<>();
        Stack<TreeNode>s=new Stack<>();
        s.push(root);
        while(!s.isEmpty()){
            TreeNode node=s.peek();
            if(node!=null){
                s.pop();
                if(node.right!=null){
                    s.push(node.right);
                }
                s.push(node);
                s.push(null);
                if(node.left!=null){
                    s.push(node.left);
                }
            }else{
                s.pop();
                node=s.pop();
                list.add(node.val);
            }
        }
        for (int i = 0; i < list.size()-1; i++) {
            if(list.get(i)>=list.get(i+1)){
                return false;
            }
        }
        return true;
    }
}
