class Solution {
    public TreeNode convertBST(TreeNode root) {
        if(root==null){
            return root;
        }
        List<Integer>list=new ArrayList<>();
        Stack<TreeNode>s=new Stack<>();
        s.push(root);
        while(!s.isEmpty()){
            TreeNode node=s.pop();
            list.add(node.val);
            if(node.left!=null){
                s.push(node.left);
            }
            if(node.right!=null){
                s.push(node.right);
            }
        }
        s.push(root);
        while(!s.isEmpty()){
            TreeNode node=s.pop();
            int temp= node.val;
            for (int n:
                 list) {
                if(n> temp){
                    node.val+=n;
                }
            }
            if(node.left!=null){
                s.push(node.left);
            }
            if(node.right!=null){
                s.push(node.right);
            }
        }
        return root;
    }
}
```



