class MyQueue {
    Stack<Integer> in;
    Stack<Integer> out;

    public MyQueue() {
        in=new Stack<>();
        out=new Stack<>();
    }

    public void push(int x) {
        in.push(x);
    }

    public int pop() {
        pour1();
        int res=out.pop();
        pour2();
        return res;
    }

    public int peek() {
        pour1();
        int res=out.peek();
        pour2();
        return res;
    }

    public boolean empty() {
        return out.empty()&&in.empty();
    }

    void pour1(){
        while(!in.empty()){
            out.push(in.pop());
        }
    }
    void pour2(){
        while(!out.empty()){
            in.push(out.pop());
        }
    }

}
