class MyStack {
    Queue<Integer>in;
    Queue<Integer>out;

    public MyStack() {
        in = new LinkedList<>();
        out = new LinkedList<>();
    }

    public void push(int x) {
        in.offer(x);
    }

    public int pop() {
        inToOut();
        int res=in.poll();
        outToIn();
        return res;
    }

    public int top() {
        inToOut();
        int res=in.peek();
        out.offer(in.poll());
        outToIn();
        return res;
    }

    public boolean empty() {
        return in.isEmpty();
    }
    void inToOut(){
        int size=in.size();
        for (int i = 0; i < size-1; i++) {
            out.offer(in.poll());
        }
    }
    void outToIn(){
        while(!out.isEmpty()){
            in.offer(out.poll());
        }
    }
}
