class Solution {
    public int evalRPN(String[] tokens) {
        Stack<Integer> s=new Stack<>();
        for (String str:
                tokens) {
            if(str.length()==1&&str.charAt(0)=='+'){
                int temp=s.pop()+s.pop();
                s.push(temp);
            } else if(str.length()==1&&str.charAt(0)=='-'){
                int t=s.pop();
                int temp=s.pop()-t;
                s.push(temp);
            }else if(str.length()==1&&str.charAt(0)=='*'){
                int temp=s.pop()*s.pop();
                s.push(temp);
            }else if(str.length()==1&&str.charAt(0)=='/'){
                int t=s.pop();
                int temp=s.pop()/t;
                s.push(temp);
            }else{
                s.push(convert(str));
            }
        }
        return s.pop();
    }
    int convert(String str){
        int res=0;
        if(str.charAt(0)=='-'){
            for (int i = str.length()-1,j=1; i >0 ; i--,j*=10) {
                res+=(str.charAt(i)-'0')*j;
            }
            res*=(-1);
        }else{
            for (int i = str.length()-1,j=1; i >=0 ; i--,j*=10) {
                res+=(str.charAt(i)-'0')*j;
            }
        }
        return res;
    }
}
