class Solution {
    public String reverseWords(String s) {
//        1.å»é™¤å¤šä½™çš„ç©ºæ ¼
        int index=0;
        StringBuilder sb=new StringBuilder();
        while(index<s.length()&&s.charAt(index)==' '){//indexæŒ‡å‘ç¬¬ä¸€ä¸ªéç©ºæ ¼å­—ç¬¦
            index++;
