class Solution {
    public boolean isHappy(int n) {
        if(n==1){
            return true;
        }
        Set<Integer> set=new HashSet<>();
        while(true){
            int sum=0;
            while(n>0){
                sum+=(n%10)*(n%10);
                n/=10;
            }
            if(sum==1){
                return true;
            }else if(set.contains(sum)){
                return false;
            }else{
                set.add(sum);
                n=sum;
                sum=0;
            }
        }
    }
}
```



