class Solution {
    public int minSubArrayLen(int target, int[] nums) {
        int len=nums.length;
        int ans=100001,sum=0;
        for(int i=0;i<len;i++){
            int sum_=0;
            for(int j=i;j<len&&sum_<target;j++){
                sum_+=nums[j];
                if(sum_>=target&&(j-i+1)<=ans){
                    ans=j-i+1;
                    sum=sum_;
                }
            }
        }
        if(ans!=100001){
            return ans;
        }else{
            return 0;
        }
    }
}
