class Solution {
    public int[][] generateMatrix(int n) {
        int [][]matrix=new int[n][n];
        int cnt=1;
        int i=0,j=-1;
        while(cnt<=n){
            matrix[i][++j]=cnt++;//ğŸ‘‰
        }
        int len=n-1;
        while(cnt<=n*n){
            for (int k = 1; k <= len; k++) {
                matrix[++i][j]=cnt++;//ğŸ‘‡
            }
            for (int k = 1; k <= len; k++) {
                matrix[i][--j]=cnt++;//ğŸ‘ˆ
            }
            len--;
            if(len>0){
                for (int k = 1; k <= len; k++) {
                    matrix[--i][j]=cnt++;//ğŸ‘†
                }
                for (int k = 1; k <= len; k++) {
                    matrix[i][++j]=cnt++;//ğŸ‘‰
                }
                len--;
            }
        }
        return matrix;
    }
}
