class Solution {
    public int[] smallestK(int[] arr, int k) {
        int[]res=new int[k];
        int len=arr.length;
        if(k==0){
            return res;
        }
        Queue<Integer> heap=new PriorityQueue<>(new Comparator<Integer>() {
            @Override
            public int compare(Integer o1, Integer o2) {
                return o2-o1;//大的先排出去
            }
        });
        for (int i = 0; i < len; i++) {
            heap.offer(arr[i]);
            if(heap.size()>k){
