class Solution {
    public ListNode mergeKLists(ListNode[] lists) {
        int len=lists.length;
        if(len==0){
            return null;
        }
        Queue<Integer>heap=new PriorityQueue<>(new Comparator<Integer>() {
            @Override
            public int compare(Integer o1, Integer o2) {
                return o1-o2;
            }
        });
        for (int i = 0; i < len; i++) {
            if(lists[i]==null){
                continue;
            }
            ListNode ptr=lists[i];
            while(ptr!=null){
                heap.offer(ptr.val);
                ptr=ptr.next;
            }
        }
        if(!heap.isEmpty()){
            ListNode res=new ListNode(heap.poll());
            ListNode ptr=res;
            while (!heap.isEmpty()){
                ListNode node=new ListNode(heap.poll());
                ptr.next=node;
                ptr=ptr.next;
            }
            return res;
        }else{
            return null;
        }
    }
}
