```java
class Solution {
    public boolean isRectangleOverlap(int[] rec1, int[] rec2) {
        if(rec1[0]<=rec2[0]&&rec1[1]<=rec2[1]) {//1
            if(rec2[0]<rec1[2]&&rec2[1]<rec1[3]){
                return true;
            }else{
                return false;
            }
        }else if(rec1[0]<=rec2[0]){//2
            if(rec2[0]<=rec1[2]&&rec2[3]>=rec1[1]){
                return true;
            }else{
                return false;
