class Solution {
    List<List<Integer>>res=new ArrayList<>();
    LinkedList<Integer> path=new LinkedList<>();
    public List<List<Integer>> subsetsWithDup(int[] nums) {
        Arrays.sort(nums);
        backtraking(0,nums);
        return res;
    }
    void backtraking(int start,int[]nums){
        res.add(new ArrayList<>(path));
