class Solution {
    public int sumOfLeftLeaves(TreeNode root) {
        int res=0;
        Stack<TreeNode>s=new Stack<>();
        if(root==null){
            return res;
        }
        s.push(root);
        while(!s.isEmpty()){
            TreeNode node=s.pop();
            if(node.left!=null){
                s.push(node.left);
                if(node.left.left==null&&node.left.right==null){
                    res+=node.left.val;
                }
            }
            if(node.right!=null){
                s.push(node.right);
            }
        }
        return res;
    }
}
```



